mapper.select:
select NAME, GENDER, JOB, JOB_NAME, CREATE_TIME, CREATOR, CREATOR_NAME, DELETED, ID, SFZH, PHONE, BIRTHDAY, ZZMM_NAME, MINZU, ZZMM, MINZU_NAME, ADDRESS, SUBJECT, SUBJECT_NAME, ENTRY_TIME, STATUS, STATUS_NAME, PIC, HONOR, WORK_AGE, WAGE, EDU, EDU_NAME from STAFF
and NAME like #{pd.NAME}
and CREATE_TIME >= #{pd.CREATE_TIME_START}
and CREATE_TIME <= #{pd.CREATE_TIME_END}
and ZZMM = #{pd.ZZMM}
and STATUS = #{pd.STATUS}
and JOB = #{pd.JOB}
order by ENTRY_TIME,ID

select NAME, GENDER, JOB, JOB_NAME, CREATE_TIME, CREATOR, CREATOR_NAME, DELETED, ID, SFZH, PHONE, BIRTHDAY, ZZMM_NAME, MINZU, ZZMM, MINZU_NAME, ADDRESS, SUBJECT, SUBJECT_NAME, ENTRY_TIME, STATUS, STATUS_NAME, PIC, HONOR, WORK_AGE, WAGE, EDU, EDU_NAME
from STAFF
and NAME like #{NAME}
and CREATE_TIME >= #{CREATE_TIME_START}
and CREATE_TIME <= #{CREATE_TIME_END}
and ZZMM = #{ZZMM}
order by CREATE_TIME desc,ID
